     */
    public function clear()
    {
        $this->id = null;
        $this->descripcion = null;
        $this->simbolo = null;
        $this->codigo = null;
        $this->alreadyInSave = false;
        $this->alreadyInValidation = false;
        $this->clearAllReferences();
        $this->resetModified();
        $this->setNew(true);
        $this->setDeleted(false);
    }

    /**
     * Resets all references to other model objects or collections of model objects.
     *
     * This method is a user-space workaround for PHP's inability to garbage collect
     * objects with circular references (even in PHP 5.3). This is currently necessary
     * when using Propel in certain daemon or large-volumne/high-memory operations.
     *
     * @param boolean $deep Whether to also clear the references on all referrer objects.
     */
    public function clearAllReferences($deep = false)
    {
        if ($deep) {
            if ($this->collRequerimientosRelatedByMonedaId) {
                foreach ($this->collRequerimientosRelatedByMonedaId as $o) {
                    $o->clearAllReferences($deep);
                }
            }
            if ($this->collRequerimientosRelatedByMonedaIngreso) {
                foreach ($this->collRequerimientosRelatedByMonedaIngreso as $o) {
                    $o->clearAllReferences($deep);
                }
            }
            if ($this->collRequerimientosRelatedByMonedaEgresos) {
                foreach ($this->collRequerimientosRelatedByMonedaEgresos as $o) {
                    $o->clearAllReferences($deep);
                }
            }
            if ($this->collPropiedads) {
                foreach ($this->collPropiedads as $o) {
                    $o->clearAllReferences($deep);
                }
            }
        } // if ($deep)

        if ($this->collRequerimientosRelatedByMonedaId instanceof PropelCollection) {
            $this->collRequerimientosRelatedByMonedaId->clearIterator();
        }
        $this->collRequerimientosRelatedByMonedaId = null;
        if ($this->collRequerimientosRelatedByMonedaIngreso instanceof PropelCollection) {
            $this->collRequerimientosRelatedByMonedaIngreso->clearIterator();
        }
        $this->collRequerimientosRelatedByMonedaIngreso = null;
        if ($this->collRequerimientosRelatedByMonedaEgresos instanceof PropelCollection) {
            $this->collRequerimientosRelatedByMonedaEgresos->clearIterator();
        }
        $this->collRequerimientosRelatedByMonedaEgresos = null;
        if ($this->collPropiedads instanceof PropelCollection) {
            $this->collPropiedads->clearIterator();
        }
        $this->collPropiedads = null;
    }

