     *
     * If the $criteria is not null, it is used to always fetch the results from the database.
     * Otherwise the results are fetched from the database the first time, then cached.
     * Next time the same method is called without $criteria, the cached collection is returned.
     * If this Requerimiento is new, it will return
     * an empty collection or the current collection; the criteria is ignored on a new object.
     *
     * @param Criteria $criteria optional Criteria object to narrow the query
     * @param PropelPDO $con optional connection object
     * @return PropelObjectCollection|Negocio[] List of Negocio objects
     * @throws PropelException
     */
    public function getNegocios($criteria = null, PropelPDO $con = null)
    {
        $partial = $this->collNegociosPartial && !$this->isNew();
        if (null === $this->collNegocios || null !== $criteria  || $partial) {
            if ($this->isNew() && null === $this->collNegocios) {
                // return empty collection
                $this->initNegocios();
            } else {
                $collNegocios = NegocioQuery::create(null, $criteria)
                    ->filterByRequerimiento($this)
                    ->find($con);
                if (null !== $criteria) {
                    if (false !== $this->collNegociosPartial && count($collNegocios)) {
                      $this->initNegocios(false);

                      foreach($collNegocios as $obj) {
                        if (false == $this->collNegocios->contains($obj)) {
                          $this->collNegocios->append($obj);
                        }
                      }

                      $this->collNegociosPartial = true;
                    }

                    return $collNegocios;
                }

                if($partial && $this->collNegocios) {
                    foreach($this->collNegocios as $obj) {
                        if($obj->isNew()) {
                            $collNegocios[] = $obj;
                        }
                    }
                }

                $this->collNegocios = $collNegocios;
                $this->collNegociosPartial = false;
            }
        }

        return $this->collNegocios;
