    /**
     * Sets a collection of Negocio objects related by a one-to-many relationship
     * to the current object.
     * It will also schedule objects for deletion based on a diff between old objects (aka persisted)
     * and new objects from the given Propel collection.
     *
     * @param PropelCollection $negocios A Propel collection.
     * @param PropelPDO $con Optional connection object
     */
    public function setNegocios(PropelCollection $negocios, PropelPDO $con = null)
    {
        $this->negociosScheduledForDeletion = $this->getNegocios(new Criteria(), $con)->diff($negocios);

        foreach ($this->negociosScheduledForDeletion as $negocioRemoved) {
            $negocioRemoved->setRequerimiento(null);
        }

        $this->collNegocios = null;
        foreach ($negocios as $negocio) {
            $this->addNegocio($negocio);
        }

        $this->collNegocios = $negocios;
        $this->collNegociosPartial = false;
    }

    /**
     * Returns the number of related Negocio objects.
     *
     * @param Criteria $criteria
     * @param boolean $distinct
     * @param PropelPDO $con
     * @return int             Count of related Negocio objects.
     * @throws PropelException
     */
    public function countNegocios(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
    {
        $partial = $this->collNegociosPartial && !$this->isNew();
        if (null === $this->collNegocios || null !== $criteria || $partial) {
            if ($this->isNew() && null === $this->collNegocios) {
                return 0;
            } else {
                if($partial && !$criteria) {
                    return count($this->getNegocios());
                }
                $query = NegocioQuery::create(null, $criteria);
                if ($distinct) {
                    $query->distinct();
                }

                return $query
                    ->filterByRequerimiento($this)
                    ->count($con);
            }
        } else {
            return count($this->collNegocios);
        }
    }

    /**
