    public function addNegocio(Negocio $l)
    {
        if ($this->collNegocios === null) {
            $this->initNegocios();
            $this->collNegociosPartial = true;
        }
        if (!$this->collNegocios->contains($l)) { // only add it if the **same** object is not already associated
            $this->doAddNegocio($l);
        }

        return $this;
    }

    /**
     * @param	Negocio $negocio The negocio object to add.
     */
    protected function doAddNegocio($negocio)
    {
        $this->collNegocios[]= $negocio;
        $negocio->setRequerimiento($this);
    }

    /**
     * @param	Negocio $negocio The negocio object to remove.
     */
    public function removeNegocio($negocio)
    {
        if ($this->getNegocios()->contains($negocio)) {
            $this->collNegocios->remove($this->collNegocios->search($negocio));
            if (null === $this->negociosScheduledForDeletion) {
                $this->negociosScheduledForDeletion = clone $this->collNegocios;
                $this->negociosScheduledForDeletion->clear();
            }
            $this->negociosScheduledForDeletion[]= $negocio;
            $negocio->setRequerimiento(null);
        }
    }


