     * when using Propel in certain daemon or large-volumne/high-memory operations.
     *
     * @param boolean $deep Whether to also clear the references on all referrer objects.
     */
    public function clearAllReferences($deep = false)
    {
        if ($deep) {
            if ($this->collDireccionRequerimientos) {
                foreach ($this->collDireccionRequerimientos as $o) {
                    $o->clearAllReferences($deep);
                }
            }
            if ($this->collNegocios) {
                foreach ($this->collNegocios as $o) {
                    $o->clearAllReferences($deep);
                }
            }
        } // if ($deep)

        if ($this->collDireccionRequerimientos instanceof PropelCollection) {
            $this->collDireccionRequerimientos->clearIterator();
        }
        $this->collDireccionRequerimientos = null;
        if ($this->collNegocios instanceof PropelCollection) {
            $this->collNegocios->clearIterator();
        }
        $this->collNegocios = null;
        $this->aMoneda = null;
    }

