    /**
     * Selects a collection of Requerimiento objects pre-filled with their Moneda objects.
     * @param      Criteria  $criteria
     * @param      PropelPDO $con
     * @param      String    $join_behavior the type of joins to use, defaults to Criteria::LEFT_JOIN
     * @return array           Array of Requerimiento objects.
     * @throws PropelException Any exceptions caught during processing will be
     *		 rethrown wrapped into a PropelException.
     */
    public static function doSelectJoinMoneda(Criteria $criteria, $con = null, $join_behavior = Criteria::LEFT_JOIN)
    {
        $criteria = clone $criteria;

        // Set the correct dbName if it has not been overridden
        if ($criteria->getDbName() == Propel::getDefaultDB()) {
            $criteria->setDbName(RequerimientoPeer::DATABASE_NAME);
        }

        RequerimientoPeer::addSelectColumns($criteria);
        $startcol = RequerimientoPeer::NUM_HYDRATE_COLUMNS;
        MonedaPeer::addSelectColumns($criteria);

        $criteria->addJoin(RequerimientoPeer::MONEDA_ID, MonedaPeer::ID, $join_behavior);

        // symfony_behaviors behavior
        foreach (sfMixer::getCallables(self::getMixerPreSelectHook(__FUNCTION__)) as $sf_hook)
        {
          call_user_func($sf_hook, 'BaseRequerimientoPeer', $criteria, $con);
        }

        $stmt = BasePeer::doSelect($criteria, $con);
        $results = array();

        while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
            $key1 = RequerimientoPeer::getPrimaryKeyHashFromRow($row, 0);
            if (null !== ($obj1 = RequerimientoPeer::getInstanceFromPool($key1))) {
                // We no longer rehydrate the object, since this can cause data loss.
                // See http://www.propelorm.org/ticket/509
                // $obj1->hydrate($row, 0, true); // rehydrate
            } else {

                $cls = RequerimientoPeer::getOMClass();

                $obj1 = new $cls();
                $obj1->hydrate($row);
                RequerimientoPeer::addInstanceToPool($obj1, $key1);
            } // if $obj1 already loaded

            $key2 = MonedaPeer::getPrimaryKeyHashFromRow($row, $startcol);
            if ($key2 !== null) {
                $obj2 = MonedaPeer::getInstanceFromPool($key2);
                if (!$obj2) {

                    $cls = MonedaPeer::getOMClass();

                    $obj2 = new $cls();
                    $obj2->hydrate($row, $startcol);
                    MonedaPeer::addInstanceToPool($obj2, $key2);
                } // if obj2 already loaded

                // Add the $obj1 (Requerimiento) to $obj2 (Moneda)
                $obj2->addRequerimiento($obj1);

            } // if joined row was not null

            $results[] = $obj1;
        }
        $stmt->closeCursor();

        return $results;
    }


